import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="bg-blue-900 p-4 text-white font-sans">
      <div className="container mx-auto flex justify-between items-center">
        {/* Left Side: Logo */}
        <Link to="/" className="text-2xl font-bold text-white">
          <span className="inline-block align-middle">
            <img
              src="/path/to/logo.png" 
              alt="Leanovated Logo"
              className="w-8 h-8 inline-block mr-2"
            />
          </span>
          Leanovated
        </Link>
        
        {/* Middle Section: Navigation Links */}
        <div className="hidden md:flex space-x-6">
          <Link to="/" className="text-white hover:text-gray-300">Home</Link>
          <Link to="/about" className="text-white hover:text-gray-300">About Us</Link>
          <Link to="/services" className="text-white hover:text-gray-300">Services</Link>
          <Link to="/portfolio" className="text-white hover:text-gray-300">Portfolio</Link>
          <Link to="/careers" className="text-white hover:text-gray-300">Careers</Link>
        </div>
        
        {/* Right Side: User Auth Links */}
        <div className="flex items-center space-x-4">
          {user ? (
            <>
              <Link to="/dashboard" className="text-white hover:text-gray-300">Dashboard</Link>
              <Link to="/organizations" className="text-white hover:text-gray-300">Organizations</Link>
              <Link to="/projects" className="text-white hover:text-gray-300">Projects</Link>
              <button
                onClick={logout}
                className="bg-red-600 px-4 py-2 rounded-md hover:bg-red-500 transition-colors"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-white hover:text-gray-300">Login</Link>
              <Link
                to="/register"
                className="bg-green-500 px-4 py-2 rounded-md hover:bg-green-400 transition-colors text-white"
              >
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
