import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="bg-blue-900 p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo/Brand Name */}
        <Link to="/" className="text-white text-2xl font-bold hover:text-gray-300 transition-colors duration-300">
          Project Manager
        </Link>

        {/* Navigation Links */}
        <div className="flex space-x-6">
          {user ? (
            <>
              <Link to="/dashboard" className="text-white text-lg hover:text-gray-300 transition-colors duration-300">
                Dashboard
              </Link>
              <Link to="/organizations" className="text-white text-lg hover:text-gray-300 transition-colors duration-300">
                Organizations
              </Link>
              <Link to="/projects" className="text-white text-lg hover:text-gray-300 transition-colors duration-300">
                Projects
              </Link>
              <button
                onClick={logout}
                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-500 transition-colors duration-300"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-white text-lg hover:text-gray-300 transition-colors duration-300">
                Login
              </Link>
              <Link to="/register" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-500 transition-colors duration-300">
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
